import { useState, useEffect } from 'react'
import Animal from './Animal'
import AnimalDetails from './AnimalDetails'

const AnimalList = () => {
    const [animals, setAnimals] = useState([])
    const [allAnimals, setAllAnimals] = useState([])

    useEffect(()=>{
        fetch("http://localhost:9292/animal")
        .then(response=>response.json())
        .then(data=>setAnimals(data))
    },[])
    console.log(animals);
    const animalArray = animals.map((animal)=>{
        return <div>
            <img src= {animal.image} alt='animal' ></img>
             <h2>{animal.name}, {animal.age}, {animal.breed}, {animal.gender}</h2>
        </div>


    })
  return (
    <div>  
        {/* <div className="card-container" style={{ display: "flexbox",  }}>{animalArray}</div> */}
        {animalArray}
        <AnimalDetails
        animals={allAnimals}
      />
      <div className="flex flex-wrap w-3/4 mx-auto">

{animals.map((animal, index) => {
  let imageUrl = animal.primary_photo_cropped?.small;
  return (
    <Animal
      key={index}
      image={
        imageUrl
          ? imageUrl
          : "https://i2-prod.mirror.co.uk/incoming/article9691512.ece/ALTERNATES/s1200b/Main-CatsandDogs.jpg"
      }
      name={animal.name ? animal.name : "Milo"}
      breed={animal.breeds.primary}
    />
  );
})}
 <AnimalDetails
animals={allAnimals}
/>
      </div>
    </div>
  )
}

export default AnimalList
