                  {animal.name}
                </Link>
              </div>
            </div>


    })
  return (
    <div>
        <h1>Animal List</h1>
        <div className="card-container" style={{ display: "flexbox",  }}>{animalArray}</div>
        {/* {animalArray} */}
    </div>
  )
}

export default AnimalList
